import {Component} from 'react';
import './App.css';

class Player extends Component{
    constructor(props){
        super(props);

        this.state={
            playerName: props.PlayerName,
            position: props.Position,
            secsLeft: props.SecsLeft,
            stop: false
        };
    };

    startTimer = () => {
        const now = Date.now();
        const then = now + this.state.secsLeft * 1000;
        console.log({then, now})

        let countdown;
        countdown = setInterval(() => {
            const secondsLeft = Math.round((then - Date.now()) / 1000);
            console.log({secondsLeft})
            //check if we should stop the timer
            if(this.state.stop){
                clearInterval(countdown);
                return;
            };
            this.setState({
                secsLeft: secondsLeft
            });
        }, 1000);
    };

    displayTime = (secsLeft) => {
        const minutes = Math.floor(secsLeft / 60);
        const remainderLeft = secsLeft % 60;
        return `${minutes}:${remainderLeft < 10 ? '0' : ''}${remainderLeft}`;
    }

    stopTimer = () => {
        this.setState({
            stop: true
        })
    }

    render(){
        return(
            <div>
                <p>{this.displayTime(this.state.secsLeft)}</p>
                <button onClick={this.startTimer}>Start Timer</button>
                <button onClick={this.stopTimer}>Stop Timer</button>
            </div>
        );
    }
}

export default Player;
