import React, {useState} from 'react';
import './App.css';
import Player from './Player.js'

export default function PlayerList({ Players, HandleOrderChange, HandleNextTurn, HandleNameChange}){

    const [players, setPlayers] = useState([...Players]);

    function handleInternalPosition(oldPos, newPos) {
        console.log(oldPos, newPos);
        const myArray = [...players];
        myArray[oldPos].Position = newPos;
        setPlayers(myArray);
    }

    function handleOrder(){
        const myArray = [...players].sort((a,b) => a.Position > b.Position ? 1 : -1);
        myArray[0].Active = true;
        HandleOrderChange(myArray);
    }

    return(
        <div>
            {Players.map(player => {
                return <Player key={player.Position} player={player} HandleNextTurn={HandleNextTurn} HandleNameChange={HandleNameChange} HandleInternalPosition={handleInternalPosition}/>
            })}
            <div className='changeOrder' style={{display: 'none'}}>
                <p>Change Player Order</p>
                <button onClick={handleOrder}>Set Order</button>
            </div>
        </div>
    )
}
