import {Component} from 'react';
import './App.css';
import Player from './Player.js'


class Game extends Component{
    constructor(props){
        super(props);

        this.state={
            noPlayers: 0,
            players: []
        }
    }


    createPlayers = (NoPlayers) => {
        const newPlayers = [];
        for (let i=0; i<NoPlayers; i++){
            const newPlayer = new Player();
            newPlayers.push(newPlayer);
        }
        this.setState({
            players: [...newPlayers]
        })

    }

    sortPlayers = () => {
        const newPlayers = [...this.state.players];
        newPlayers.sort((a,b) => a.position > b.position ? 1 : -1);
        this.setState({
            players: [...newPlayers]
        });
    }

    render(){
        return(
            <div>
                {this.state.noPlayers}
            </div>
        );
    }
}

export default Game;


