import {Component} from 'react';
import Counter from './Counter';

class TotalScore extends Component{
    state = {
        behinds: 0,
        goals: 0
    };

    increaseBehinds = () => {
        this.setState({
            behinds: this.state.behinds + 1
        });
    }
    decreaseBehinds = () => {
        this.setState({
            behinds: this.state.behinds - 1
        });
    }
    increaseGoals = () => {
        this.setState({
            goals: this.state.goals + 1
        });
    }
    decreaseGoals = () => {
        this.setState({
            goals: this.state.goals - 1
        });
    }

    render(){
        return(
            <div>
                <div onClick={this.decreaseBehinds}>My Text: {this.state.behinds}</div>
                <Counter />
            </div>
        );
    }
}

export default TotalScore;
