import React, {useState} from 'react';
import './App.css';
import Player from './Player.js';
import PlayerList from './PlayerList.js';

export default function Game({NoPlayers, TimeAllowed}){

    const [players, setPlayers] = useState([]);

    setPlayers(() => {
        const myArray = [];
        for (let i=0; i<NoPlayers; i++){
            myArray[i] = {Position: i, TimeAllowed: TimeAllowed};
        }
        return myArray;
    });

    return(
        <div>
            <PlayerList
                players={players}
            />
        </div>
    );
}



