import {useState, useRef} from 'react';
import './App.css';
import Game from './Game.js';

function PlayerSetup(){

    const [noPlayers, setNoPlayers] = useState([]);
    const [timeAllowed, setTimeAllowed] = useState([]);

    const noPlayersRef = UseRef();

    handlePlayers = (e) => {
        e.preventDefault();
        if (!(e.current.value > 1 && e.current.value <= 6)) {
            alert('Invalid amount of Players, must be between 2 and 6');
            return;
        };
        noPlayersRef = e.current.value;
        setNoPlayers(() => {
            return noPlayersRef;
        });
        document.querySelector('#StartForm').style.display = 'none';
        document.querySelector('#TimeForm').style.display = 'block';
        const names = document.querySelectorAll('.nameInput');
        names.forEach(name => name.disabled = false);
        // const output = [];
        // output.push(<Game key={0}
        //         NoPlayers = {this.state.noPlayers}
        //     />)
        // this.setState({
        //     row: output
        // })
    }

    const timeAllowedRef = UseRef();

    handleTime = (e) => {
        e.preventDefault();
        if (!(this.state.timeAllowed > 1 && this.state.timeAllowed <= 300)) {
            alert('Invalid amount of Time, must be between 1 and 300');
            return;
        };
        document.querySelector('#TimeForm').style.display = 'none';
        const nextButtons = document.querySelectorAll('.nextButton');
        nextButtons.forEach(button => {
            button.disabled = false;
        })
        // const output = [];
        // output.push(<Game key={0}
        //         NoPlayers = {this.state.noPlayers}
        //     />)
        // this.setState({
        //     row: output
        // })
    }

    return(
        <div>
          <form onSubmit={this.handlePlayers} id="StartForm">
            <label>
              Number of Players:
              <input type="text" ref={noPlayersRef} />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <form onSubmit={this.handleTime} style={{display: 'none'}} id="TimeForm">
            <label>
              Amount of Time (Minutes):
              <input type="number" ref={timeAllowedRef} />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <div>
            <Game
                NoPlayers = {this.state.noPlayers}
                TimeAllowed = {this.state.timeAllowed}
            />
          </div>
        </div>
    );


}

export default PlayerSetup;
