import {Component} from 'react';
import './App.css';
import Player from './Player.js'

class Game extends Component{
    constructor(props){
        super(props);

        this.state={
            noPlayers: props.NoPlayers
        };
    };


    createPlayers = () => {
        const output = [];
        for (let i=0; i<this.state.noPlayers;i++){
            output.push(<Player key={i} />);
        }
        return output;
    }

    render(){
        return(
            <div>
                {this.createPlayers}
            </div>
        );
    }


}

export default Game;
