import {Component} from 'react';
import './App.css';
import Player from './Player.js';

class PlayerSetup extends Component{
    constructor(props){
        super(props);

        this.state={
            nPlayers: 0,
            players: []
        };
    };

    setNPlayers = (e) => {
        if (!e.target.nPlayers || e.target.nPlayers <= 0 || e.target.nPlayers > 6) return;
        const list = [];
        for (let i=0; i < e.target.nPlayers; i++){
            const player = {playerName: '', position: i, secsLeft: 0};
            list.push(player);
        }
        this.setState({
            nPlayers: e.target.nPlayers,
            players: list
        })
        console.log(list);
    }

    render(){
        return(
        <div>
            <form onSubmit={this.setNPlayers}>
                <fieldset>
                    <label for="nPlayers">Number of Players</label>
                    <input type="number" id="nPlayers" /><br/>
                    <button>Submit</button>
                </fieldset>
            </form>
        </div>

        );
    }


}

export default PlayerSetup;
