import {Component} from 'react';
import './App.css';
import Player from './Player.js'

export default function Game({NoPlayers}){
    const players = [];
    for (let i=0; i<NoPlayers; i++){
        const newPlayer = new Player();
        players.push(newPlayer);
    }


    return(
        <div>
            {players.map((player) =>(
                {player}
            ))}
        </div>
    );
};





