import React, {useState, useRef} from 'react';
import './App.css';
import PlayerList from './PlayerList.js';

function PlayerSetup(){

    const [numPlayers, setNumPlayers] = useState(0);
    const [timeAllowed, setTimeAllowed] = useState(0);
    const [players, setPlayers] = useState([]);
    const [counter, setCounter] = useState(0);

    const noPlayersRef = useRef();

    function handlePlayers(e) {
        e.preventDefault();
        const noPlayers = noPlayersRef.current.value;
        if (!(noPlayers > 1 && noPlayers <= 6)) {
            alert('Invalid amount of Players, must be between 2 and 6');
            return;
        };
        setNumPlayers(noPlayers);
        const myArray = [];
        for (let i=0; i<noPlayers; i++){
            myArray.push({Name: `Player ${i+1}`, Position: i+1, TimeAllowed: timeAllowed, Active: false});
        }
        setPlayers(myArray);
    }

    const timeAllowedRef = useRef();

    function handleTime(e) {
        e.preventDefault();
        const timeLimit = timeAllowedRef.current.value;
        if (!(timeLimit > 1 && timeLimit <= 300)) {
            alert('Invalid amount of Time, must be between 1 and 300');
            return;
        };
        setTimeAllowed(timeLimit);
        const myArray = [...players];
        for (let i=0; i<numPlayers; i++){
            myArray[i].TimeAllowed = timeLimit*60;
        }
        setPlayers(myArray);
    }

    function handleStartGame(e){
        if (timeAllowed === 0 || numPlayers === 0){
            alert('Set a valid amount of Players and Time');
            return;
        };
        const myArray = [...players];
        myArray[0].Active = true;
        document.querySelector('#StartForm').style.display = 'none';
        document.querySelector('#TimeForm').style.display = 'none';
        document.querySelector('#StartGame').style.display = 'none';
    }

    function handleNextTurn(e){
        console.log("I'm handling Next Turn");
        const myArray = [...players];
        myArray[counter].Active = false;
        const nextCounter = (counter + 1) % numPlayers;
        myArray[nextCounter].Active = true;
        setCounter(nextCounter);
    }


    function handleOrderChange(e){
        console.log("I'm handling Order Change");
        const myArray = [...players].sort((a,b) => a.Position > b.Position ? 1 : -1);
        setPlayers(myArray);
    }

    function handleNameChange(newName, position){
        const myArray = [...players];
        console.log(newName, position);
        // myArray[position].Name = newName;
        // setPlayers(myArray);
    }

    function handlePosChange(oldPos, newPos){
        const myArray = [...players];
        myArray[oldPos].Position = newPos;
        setPlayers(myArray);
    }

    return(
        <div style={{margin: '10px'}}>
          <form onSubmit={handlePlayers} id="StartForm">
            <label>
              Number of Players:
              <input type="number" ref={noPlayersRef} />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <form onSubmit={handleTime} id="TimeForm">
            <label>
              Amount of Time (Minutes):
              <input type="number" ref={timeAllowedRef} />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <button onClick={handleStartGame} id="StartGame">Start Game</button>
          <div>
            <PlayerList
                Players = {players}
                HandleOrderChange = {handleOrderChange}
                HandleNextTurn = {handleNextTurn}
                HandleNameChange = {handleNameChange}
                HandlePosChange = {handlePosChange}
            />
          </div>
        </div>
    );


}

export default PlayerSetup;



