import {Component} from 'react';
import './App.css';
import Player from './Player.js';
import Game from './Game.js';

class PlayerSetup extends Component{
    constructor(props){
        super(props);

        this.state={
            noPlayers: 0,
            row: []
        };
    };


    handleChange = (e) => {
        this.setState({
            noPlayers: e.target.value
        })
    }

    handleSubmit = (e) => {
        console.log("Running handleSubmit");
        e.preventDefault();
        if (this.state.noPlayers <= 1 || this.state.noPlayers > 6) {
            alert('Invalid amount of Players');
            return;
        };
        const output = [];
        output.push(<Game key={0}
                NoPlayers = {this.state.noPlayers}
            />)
        this.setState({
            row: output
        })
    }

    render(){
        return(
        <div>
          <form onSubmit={this.handleSubmit}>
            <label>
              Number of Players:
              <input type="text" value={this.state.nPlayers} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <div>
            <Game
                NoPlayers = {this.state.noPlayers}
            />
          </div>
        </div>

        );
    }


}

export default PlayerSetup;
