import React, {useState} from 'react';
import './App.css';
import CountdownClock from './CountdownClock.js'


export default function Player({ player, HandleNextTurn, HandleNameChange, HandlePosChange}){

    const [name, setName] = useState(player.Name);
    const [position, setPosition] = useState(player.Position);

    function handleName(e){
        HandleNameChange(e.target.value, position);
        setName(e.target.value);
    }

    function handlePosition(e){
        HandlePosChange(position,e.target.value);
        setPosition(e.target.value);
    }

    return(
        <div>
            <input value={name} onChange={handleName} className='name'/>
            <input value={position} onChange={handlePosition} className='position'/>
            <CountdownClock
                SecsLeft = {player.TimeAllowed}
            />
            <button onClick={HandleNextTurn}> > </button>
        </div>
    )
}


