import {Component} from 'react';
import './App.css';
import Player from './Player.js'


class Game extends Component{
    constructor(props){
        super(props);

        const myPlayers = [];
        for (let i=0; i<props.NoPlayers; i++){
            const newPlayer = new Player();
            myPlayers.push(newPlayer);
        }

        this.state={
            noPlayers: props.NoPlayers,
            players: [...myPlayers]
        }
    }

    sortPlayers = () => {
        const newPlayers = [...this.state.players];
        newPlayers.sort((a,b) => a.position > b.position ? 1 : -1);
        this.setState({
            players: [...newPlayers]
        });
    }

    render(){
        return(
            <div>
                {this.state.players.map((player, index) =>(
                    <div key={index}>{player.render()}</div>
                ))}
            </div>
        );
    }
}

export default Game;


