import {Component} from 'react';
import './App.css';

class Player extends Component{
    constructor(props){
        super(props);

        this.state={
            playerName: '',
            secsLeft: 0,
            position: 0,
            stop: false
        };
    };

    changeName = (e) => {
        this.setState({
            playerName: e.target.value
        })
    };

    setTimer = (e) => {
        let secs = 0;
        const myArray = e.target.value.split(':');
        secs += parseInt(myArray[0]) * 60;
        this.setState({
            secsLeft: secs
        })
        console.log(this.state.secsLeft);
    }

    startTimer = () => {
        const now = Date.now();
        const then = now + this.state.secsLeft * 1000;

        let countdown;
        countdown = setInterval(() => {
            //check if we should stop the timer
            if(this.state.stop){
                clearInterval(countdown);
                return;
            };
            const secondsLeft = Math.round((then - Date.now()) / 1000);
            this.setState({
                secsLeft: secondsLeft
            });
        }, 1000);
    };

    displayTime = (secsLeft) => {
        const minutes = Math.floor(secsLeft / 60);
        const remainderLeft = secsLeft % 60;
        return `${minutes}:${remainderLeft < 10 ? '0' : ''}${remainderLeft}`;
    }

    stopTimer = () => {
        this.setState({
            stop: true
        })
    }

    render(){
        return(
            <div style={{display: "inline-block"}}>
                <form style={{display: "inline-block"}}>
                    <label>
                        Name:
                        <input type="text" value={this.state.playerName} onChange={this.changeName} disabled={true} className='nameInput'/>
                    </label>
                </form>
                <p style={{display: "inline-block"}} className='time'>{this.displayTime(this.state.secsLeft)}</p>
                <button onClick={this.startTimer} disabled={true} className='nextButton'>Start Timer</button>
                <button onClick={this.stopTimer} disabled={true} className='nextButton'>Stop Timer</button>
            </div>
        );
    }
}

export default Player;
