import {Component} from 'react';
import './App.css';
import Player from './Player.js'


class Game extends Component{
    constructor(props){
        super(props);

        const myArray = [];
        for (let i=0; i<NoPlayers; i++){
            myArray[i] = i;
        }

        this.state={
            noPlayers: NoPlayers,
            array: myArray
        }
    }

    render(){
        return(
            <div>
                {this.state.array.map(index => (
                    <div key={index}><Player /></div>
                ))}
            </div>
        );
    }

}

export default Game;


