import {Component} from 'react';
import './App.css';
import Game from './Game.js';

class PlayerSetup extends Component{
    constructor(props){
        super(props);

        this.state={
            noPlayers: 0,
            timeAllowed: 0
        };
    };


    handleChange = (e) => {
        this.setState({
            noPlayers: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        if (!(this.state.noPlayers > 1 && this.state.noPlayers <= 6)) {
            alert('Invalid amount of Players, must be between 2 and 6');
            return;
        };
        document.querySelector('#StartForm').style.display = 'none';
        document.querySelector('#TimeForm').style.display = 'block';
        const names = document.querySelectorAll('.nameInput');
        names.forEach(name => name.disabled = false);
        // const output = [];
        // output.push(<Game key={0}
        //         NoPlayers = {this.state.noPlayers}
        //     />)
        // this.setState({
        //     row: output
        // })
    }

    handleTimeChange = (e) => {
        this.setState({
            timeAllowed: e.target.value
        })
        const times = document.querySelectorAll('.time');
        times.forEach(time => time.innerHTML = this.displayTime(e.target.value * 60));
    }

    handleTime = (e) => {
        e.preventDefault();
        if (!(this.state.timeAllowed > 1 && this.state.timeAllowed <= 300)) {
            alert('Invalid amount of Time, must be between 1 and 300');
            return;
        };
        document.querySelector('#TimeForm').style.display = 'none';
        // const output = [];
        // output.push(<Game key={0}
        //         NoPlayers = {this.state.noPlayers}
        //     />)
        // this.setState({
        //     row: output
        // })
    }

    displayTime = (secsLeft) => {
        const minutes = Math.floor(secsLeft / 60);
        const remainderLeft = secsLeft % 60;
        return `${minutes}:${remainderLeft < 10 ? '0' : ''}${remainderLeft}`;
    }

    render(){
        return(
        <div>
          <form onSubmit={this.handleSubmit} id="StartForm">
            <label>
              Number of Players:
              <input type="text" value={this.state.noPlayers} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <form onSubmit={this.handleTime} style={{display: 'none'}} id="TimeForm">
            <label>
              Amount of Time (Minutes):
              <input type="number" value={this.state.timeAllowed} onChange={this.handleTimeChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <div>
            <Game
                NoPlayers = {this.state.noPlayers}
            />
          </div>
        </div>

        );
    }


}

export default PlayerSetup;
