import {Component} from 'react';
import './App.css';
import Game from './Game.js';

class PlayerSetup extends Component{
    constructor(props){
        super(props);

        this.state={
            noPlayers: 0,
        };
    };


    handleChange = (e) => {
        this.setState({
            noPlayers: e.target.value
        })
    }

    handleSubmit = (e) => {
        e.preventDefault();
        if (!(this.state.noPlayers > 1 && this.state.noPlayers <= 6)) {
            alert('Invalid amount of Players, must be between 2 and 6');
            return;
        };
        document.querySelector('#StartForm').style.display = 'none';
        const names = document.querySelectorAll('.nameInput');
        names.forEach(name => name.disabled = false);
        // const output = [];
        // output.push(<Game key={0}
        //         NoPlayers = {this.state.noPlayers}
        //     />)
        // this.setState({
        //     row: output
        // })
    }

    render(){
        return(
        <div>
          <form onSubmit={this.handleSubmit} id="StartForm">
            <label>
              Number of Players:
              <input type="text" value={this.state.noPlayers} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>
          <div>
            <Game
                NoPlayers = {this.state.noPlayers}
            />
          </div>
        </div>

        );
    }


}

export default PlayerSetup;
