import {Component} from 'react';
import './App.css';
import Player from './Player.js';

class PlayerSetup extends Component{
    constructor(props){
        super(props);

        this.state={
            nPlayers: 0,
            players: []
        };
    };


    handleChange = (e) => {
        this.setState({
            nPlayers: e.target.value
        })
    }

    handleSubmit = (e) => {
        console.log("Running handleSubmit");
        e.preventDefault();
        if (this.state.nPlayers <= 0 || this.state.nPlayers > 6) {
            alert('Invalid amount of Players');
            return;
        };
        const list = [];
        for (let i=0; i < this.state.nPlayers; i++){
            const player = {playerName: '', position: i, secsLeft: 0};
            list.push(player);
            console.log(player);
        };
        this.setState({
            players: list
        });
    }

    render(){
        return(
        <div>
          <form onSubmit={this.handleSubmit}>
            <label>
              Name:
              <input type="text" value={this.state.nPlayers} onChange={this.handleChange} />
            </label>
            <input type="submit" value="Submit" />
          </form>

            <form onSubmit={this.setNPlayers}>
                <div>
                    <label htmlFor="nPlayers">Number of Players</label>
                    <input type="number" id="nPlayers" /><br/>
                    <button type="submit">Submit</button>
                </div>
            </form>
        </div>

        );
    }


}

export default PlayerSetup;
