import {Component} from 'react';

class TotalScore extends Component{
    state = {
        behinds: 0,
        goals: 0
    };
    increaseBehinds(){
        this.setState({
            behinds: this.state.behinds + 1
        });
    }
    decreaseBehinds(){
        this.setState({
            behinds: this.state.behinds - 1
        });
    }
    increaseGoals(){
        this.setState({
            behinds: this.state.goals + 1
        });
    }
    decreaseGoals(){
        this.setState({
            behinds: this.state.goals - 1
        });
    }

    render(){
        return(
            <div>My Text: {this.state.behinds}</div>
        );
    }
}

export default TotalScore;
