import React, {useState, useEffect} from 'react';
import './App.css';


export default function CountdownClock({SecsLeft}){

    const [timeString, setTimeString] = useState('');

    useEffect(() =>{
        setTimeString(() => {
            let newString = ''
            let remainderLeft = parseInt(SecsLeft);
            if (remainderLeft >=3600){
                const hours = Math.floor(remainderLeft / 3600);
                remainderLeft = remainderLeft % 3600;
                newString += `${hours}:`;
            }
            const minutes = Math.floor(remainderLeft / 60);
            remainderLeft = remainderLeft % 60;
            newString += `${minutes}:${remainderLeft < 10 ? '0' : ''}${remainderLeft}`;
            console.log(newString);
            return newString;
        });
    }, [])


    return(
        <div style={{display: "inline-block"}}>
            <p style={{display: "inline-block"}} className='time'>{timeString}</p>
        </div>
    )
}


