import React, {useState, useRef} from 'react';
import './App.css';
import CountdownClock from './CountdownClock.js'


export default function Player({ Player }){

    const [name, setName] = useState(Player.Name);
    const [position, setPosition] = useState(Player.Position);
    const [active, setActive] = useState(false);
    const [secsLeft, setSecsLeft] = useState(Player.TimeAllowed);

    function handleName(e){
        setName(e.target.value);
    }

    function handlePosition(e){
        setPosition(e.target.value);
    }

    function handleActive(e){
        setActive(!active);
        setSecsLeft(secsLeft - 1);
    }


    return(
        <div>
            <input value={name} onChange={handleName}/>
            <input value={position} onChange={handlePosition}/>
            <CountdownClock
                SecsLeft = {secsLeft}
            />
            <button onClick={handleActive}>Start/Stop</button>
        </div>
    )
}


